    "SELECT   TOP (10) dbo.tContent.id, DATEDIFF(second, dbo.tContent.regDatetime, GETDATE()) AS seconds,  " + 
    "                dbo.tContent.userId, dbo.tContent.status, dbo.tContent.[content], dbo.tContent.photoCount, dbo.tContent.photo1,  " + 
    "                dbo.tContent.photo2, dbo.tContent.photo3, dbo.tContent.photo4, dbo.tContent.uplinkType, dbo.tContent.likeCount,  " + 
    "                dbo.tContent.reCount, dbo.tContent.fwCount, dbo.userInfo.userName, dbo.userInfo.nickName, dbo.userInfo.photo,  " + 
    "                DATEDIFF(second, tContent_1.regDatetime, GETDATE()) AS reSeconds, tContent_1.userId AS reUserId,  " + 
    "                tContent_1.status AS reStatus, tContent_1.[content] AS reContent, tContent_1.photoCount AS rePhotoCount,  " + 
    "                tContent_1.photo1 AS rePhoto1, tContent_1.photo2 AS rePhoto2, tContent_1.photo3 AS rePhoto3,  " + 
    "                tContent_1.photo4 AS rePhoto4, tContent_1.likeCount AS reLikeCount, tContent_1.reCount AS reReCount,  " + 
    "                tContent_1.fwCount AS reFwCount, userInfo_1.userName AS reUserName, userInfo_1.nickName AS reNickName,  " + 
    "                userInfo_1.photo AS rePhoto " + 
    "FROM      dbo.userInfo RIGHT OUTER JOIN " + 
    "                dbo.tContent ON dbo.userInfo.id = dbo.tContent.userId LEFT OUTER JOIN " + 
    "                dbo.userInfo AS userInfo_1 RIGHT OUTER JOIN " + 
    "                dbo.tContent AS tContent_1 ON userInfo_1.id = tContent_1.userId ON dbo.tContent.uplinkId = tContent_1.id " + 
    "WHERE (dbo.tContent.uplinkId = " + topicId + ") OR (dbo.tContent.id = " + topicId + ") " +
    "ORDER BY dbo.tContent.id ";

sqlResult = db.executeQuery("DB", sql);
if (sqlResult == null) {
    eio.appendString8("F");
    es.logWarning("数据库执行错误, sql=" + sql);
    return;
}
