var sql,sqlResult;  

var cond = "";
if ((needRefresh == "F") &amp;&amp; (lastId != 0)) {
    cond = " (dbo.tContent.id < " + lastId + ") ";
}
if (searchString != "") {
    if (cond != "") cond += "and ";
    cond += " (dbo.tContent.content like '%" + db.encode(searchString) + "%') ";
}
if (cond != "") {
    cond = "WHERE " + cond;
}

sql = 
    "SELECT TOP (6) dbo.tContent.id, DATEDIFF(minute, dbo.tContent.regDatetime,  " + 
    "      GETDATE()) AS mins, dbo.tContent.userId, dbo.tContent.[content], dbo.tContent.photo1,  " + 
    "      dbo.tContent.likeCount,  " + 
    "      SUM(CASE WHEN tContent_1.uplinkType = 'R' THEN 1 ELSE 0 END) AS replyCount,  " + 
    "      SUM(CASE WHEN tContent_1.uplinkType = 'F' THEN 1 ELSE 0 END) AS forwardCount,  " + 
    "      dbo.userInfo.photo, dbo.userInfo.userName, dbo.userInfo.nickName,  " + 
    "      DATEDIFF(minute, tContent_2.regDatetime, GETDATE()) AS reMins,  " + 
    "      tContent_2.[content] AS reContent, tContent_2.photo1 AS rePhoto1,  " + 
    "      tContent_2.userId AS reUserId, userInfo_1.userName AS reUserName,  " + 
    "      userInfo_1.nickName AS reNickName, userInfo_1.photo AS rePhoto " + 
    "FROM dbo.userInfo AS userInfo_1 INNER JOIN " + 
    "      dbo.tContent AS tContent_2 ON userInfo_1.id = tContent_2.userId RIGHT OUTER JOIN " + 
    "      dbo.tContent INNER JOIN " + 
    "      dbo.follow ON dbo.tContent.userId = dbo.follow.followId INNER JOIN " + 
    "      dbo.userInfo ON dbo.tContent.userId = dbo.userInfo.id ON  " + 
    "      tContent_2.id = dbo.tContent.uplinkId LEFT OUTER JOIN " + 
    "      dbo.tContent AS tContent_1 ON dbo.tContent.id = tContent_1.uplinkId " + 
    cond +
    "GROUP BY dbo.tContent.id, dbo.tContent.regDatetime, dbo.tContent.userId,  " + 
    "      dbo.tContent.[content], dbo.tContent.photo1, dbo.tContent.likeCount,  " + 
    "      dbo.userInfo.photo, dbo.userInfo.userName, dbo.userInfo.nickName,  " + 
    "      tContent_2.regDatetime, tContent_2.[content], tContent_2.photo1, tContent_2.userId,  " + 
    "      userInfo_1.userName, userInfo_1.nickName, userInfo_1.photo " + 
    "ORDER BY dbo.tContent.id DESC     "; 

sqlResult = db.executeQuery("DB", sql);
if (sqlResult == null) {
    eio.appendString8("F");
    es.logWarning("数据库执行错误, sql=" + sql);
    return;
}
