if (g5.p3.p8.p1.c_userList.style.visibility=="hidden") {
    ee.changeParent(g5.p3.p8.p1.c_userList, g5.p3.p8.p1, 1);
    ee.setVisible(g5.p3.p8.p1.c_userList, true);
} else {
    ee.setVisible(g5.p3.p8.p1.c_userList, false);
}
