    root.c_button2 = c;
    c.style.cursor = "pointer";
    c.v_style = new g5.BStyle("rgba(255,255,255,0.8)","#e8f5fe","#d2ecfc","×删除","×删除","×删除","#000000","#1da1f2","#1da1f2","");
