control.onUploadStartC:
fileExt = fileExt.toLowerCase();
if ((fileExt != ".bmp") &amp;&amp;
    (fileExt != ".jpg") &amp;&amp;
    (fileExt != ".jpeg") &amp;&amp;
    (fileExt != ".png") &amp;&amp;
    (fileExt != ".tif") &amp;&amp;
    (fileExt != ".tiff")) return;

if (ee.getControl("panel51").v_photos.length>=4) {
    alert("对不起，您最多只能上传4张图片。");
    return;
}

control.onUploadStartS:
var sql,sqlResult;  
