control.onClick:
if (ee.getControl("panel51").v_photos.length>=4) {
    alert("对不起，您最多上传4张照片或者视频");
    return;
}
var url = prompt("请输入播放地址：", "");
if (url == null) return;
if (url.length<=0) return;
var ytbVideo = "";
var pos;
if ((pos=url.toLowerCase().indexOf("youtube.com/watch?v=",0))>0) {
    //https://www.youtube.com/watch?v=yJG3WMG1dak
    ytbVideo = url.substr(pos + "youtube.com/watch?v=".length, url.length);
} else if ((pos=url.toLowerCase().indexOf("youtu.be/",0))>0) {
    //https://youtu.be/edESgyDUKDU
    ytbVideo = url.substr(pos + "youtu.be/".length, url.length);
} else {
    alert("您输入的视频地址不正确");
    return;
}
if ((ytbVideo.length<=8)||(ytbVideo.length>=14)) {
    alert("您输入的视频地址不正确");
    return;
}
ytbVideo = "?YTB" + ytbVideo;
var i = 0;
for (i=0; i<ee.getControl("panel51").v_photos.length; i++) {
    var s = ee.getControl("panel51").v_photos[i];
    if (s.length<4) continue;
    if (s.substr(0,4)=="?YTB") {
        break;
    }
}
if (i>=ee.getControl("panel51").v_photos.length) {
    ee.getControl("panel51").v_photos.push(ytbVideo);
} else {
    ee.getControl("panel51").v_photos.splice(i,1,ytbVideo);
}
g5.refreshPhoto(ee.getControl("panel51"));

