    if (updateHomepage.data.getData(18,i) == "DEL") continue;
    var item = createPanel(root, "page1_" + updateHomepage.data.getData(0,i), "", 0, cy, itemWidth, 5);
    g5.loadTwitterRecord(item, updateHomepage.data, i);
    item.v_mode = "";
    g5.renderTwitter(item);
    if (mode == "N") {
        g5.itemsHome.splice(i, 0, item);
    } else {
        g5.itemsHome.push(item);
    }
    cy += 0 + eb.getDivRect(item).h;
}
if (mode == "N") {
    if (updateHomepage.data.rowCount>0) {
        var dy = cy - eb.getDivRect(g5.itemsHome[updateHomepage.data.rowCount]).y; 
        g5.homepageMoveY(updateHomepage.data.rowCount,dy);
    }
    var rect = eb.getDivRect(g5.itemsHome[g5.itemsHome.length-1]);
    cy = rect.y + rect.h;
}

if (g5.currentPageIndex == 0) {
    eb.setDivRect(ee.getControl("panel135"),null,null,null,cy);
}
eb.setDivRect(ee.getControl("panel41"),null,null,null,cy);
eb.processDock();


