    for (i=0; i<showTopic.items.length; i++) ee.removeControl(showTopic.items[i]);
    showTopic.items = new Array();
} else {
    if (showTopic.items.length>0) {
        var rect = eb.getDivRect(showTopic.items[showTopic.items.length-1]);
        cy = 2 + rect.y + rect.h;
    }
}

