taskSetItem.script3:
if (eio.readString8()!="T") {
    alert(eio.readString8());
    return;
}

showChat.data = eio.readDataSet();

if (eb.isEmpty(showChat.items)) showChat.items = new Array();
for (var i=0; i<showChat.items.length; i++) ee.removeControl(showChat.items[i]);
showChat.items = new Array();
if (showChat.data.rowCount<=0) {
    eb.processDock();
    return;
}

var fi = showChat.data.colCount;
showChat.data.setColCount(fi+1);
showChat.data.setData(fi,showChat.data.rowCount-1,1);
for (var i = showChat.data.rowCount-2; i>=0; i--) {
    if (showChat.data.getData(2,i+1)-showChat.data.getData(2,i)>300) {
        showChat.data.setData(fi,i,1);    
    } else if (showChat.data.getData(5,i+1)!=showChat.data.getData(5,i)) {
        showChat.data.setData(fi,i,1);    
    } else {
        showChat.data.setData(fi,i,showChat.data.getData(fi,i+1)+1);    
    }
}
var isBlock = false;
for (var i=0; i<showChat.data.rowCount; i++) {
    if (showChat.data.getData(fi,i)>=2) {
        if (isBlock) {
            showChat.data.setData(fi,i,3);
        } else {
            isBlock = true;
            showChat.data.setData(fi,i,4);
        }
    } else {
        if (isBlock) {
            isBlock = false;
            showChat.data.setData(fi,i,2);
        } else {
