control.onControlLoad:
g5.currentPageIndex = -1;
g5.imgUrl = "/incoming/";
g5.itemsHome = new Array(); //home
g5.itemsPageUser = new Array();
g5.newUser = false;

g5.p1 = ee.getControl("panel136");
g5.p2 = ee.getControl("panel135");
g5.p3 = ee.getControl("panel137");
g5.mainDiv = ee.getControl("panel134");

g5.lastWidth = 0;
g5.refreshMain = function() {
    var cw = $g("divRoot").offsetWidth;    
    if (g5.lastWidth == cw) return;
    g5.lastWidth = cw;
    var p1w = eb.getDivRect(g5.p1).w;
    var p2w = eb.getDivRect(g5.p2).w;
    var p3w = eb.getDivRect(g5.p3).w;
    if (cw >= 300+600+400) {
        p1w = 300;
        p2w = 600;
        p3w = 400;
    } else if (cw >= 80+600+400) {
        p1w = 80;
        p2w = 600;
        p3w = 400;
    } else if (cw >= 80+600+330) {
        p1w = 80;
        p2w = 600;
        p3w = 330;
    } else if (cw >= 80+600) {
        p1w = 80;
        p2w = 600;
        p3w = 0;
    } else {
        p1w = 80;
        p2w = cw-80;
        p3w = 0;
    }
    var left = parseInt((cw - p1w - p2w - p3w) / 2);
    ee.setRect(g5.p1,left,null,p1w,null);
    ee.setRect(g5.p2,left+p1w,null,p2w,null);
    ee.setRect(g5.p3,left+p1w+p2w,null,p3w,null);
}
setInterval(function() {g5.refreshMain();}, 100);

g5.lastScrollY = g5.mainDiv.scrollTop;
eb.addEvent(g5.mainDiv, "scroll", function() {
    var temp = g5.mainDiv.scrollTop;
    var dy = temp - g5.lastScrollY;
    g5.lastScrollY = temp;
    var ch = $g("divRoot").offsetHeight;
    var p3h = eb.getDivRect(g5.p3).h;
    temp = eb.getDivRect(g5.p3).y;
    if (p3h<=ch) {
        if (temp!=0) eb.setDivRect(g5.p3,null,0,null,null);
    } else {
        var temp1 = temp;
        if (dy>0) {
            temp1 = temp - dy;
            if (temp1<ch-p3h) temp1 = ch-p3h;
        } else if (dy<0) {
            temp1 = temp - dy;
            if (temp1>0) temp1 = 0;        
        }
        if (temp!=temp1) eb.setDivRect(g5.p3,null,temp1,null,null);
    }

    var p2h = eb.getDivRect(ee.getControl("panel135")).h;
    if (p2h>ch) {
        if ((p2h-ch-g5.mainDiv.scrollTop) < 300) {
            if (!eb.isEmpty(g5.datasetHome)) {
                if (g5.datasetHome.rowCount>0) {
                    updateHomepage();
                }
            }
        }
    }


}, false);

g5.changePage = function(pageIndex) {
    if ((pageIndex<0) || (pageIndex>=4)) return false;
    if (g5.currentPageIndex == pageIndex) return true;
    if (g5.currentPageIndex == 0) {
        ee.setImage(ee.getControl("panel1"), "/twitterG/main/d1.png", "fill", "", "");
        ee.showAni(ee.getControl("layouty2"), 1);
    } else if (g5.currentPageIndex == 1) {
        ee.setImage(ee.getControl("panel2"), "/twitterG/main/d2.png", "fill", "", "");
        ee.showAni(ee.getControl("layouty3"), 1);
    } else if (g5.currentPageIndex == 2) {
        ee.setImage(ee.getControl("panel3"), "/twitterG/main/d3.png", "fill", "", "");
    } else if (g5.currentPageIndex == 3) {
        ee.setImage(ee.getControl("panel4"), "/twitterG/main/d4.png", "fill", "", "");
        ee.showAni(ee.getControl("layouty5"), 1);
    }
    if (pageIndex == 0) {
        ee.setImage(ee.getControl("panel1"), "/twitterG/main/d1s.png", "fill", "", "");
        ee.showAni(ee.getControl("layouty2"), 0);
    } else if (pageIndex == 1) {
        ee.setImage(ee.getControl("panel2"), "/twitterG/main/d2s.png", "fill", "", "");
        ee.showAni(ee.getControl("layouty3"), 0);
    } else if (pageIndex == 2) {
        ee.setImage(ee.getControl("panel3"), "/twitterG/main/d3s.png", "fill", "", "");
    } else if (pageIndex == 3) {
        ee.setImage(ee.getControl("panel4"), "/twitterG/main/d4s.png", "fill", "", "");
        ee.showAni(ee.getControl("layouty5"), 0);
    }
    g5.currentPageIndex = pageIndex;
    return true;
}

g5.changePage2 = function(pageIndex) {
    if ((pageIndex<0) || (pageIndex>=2)) return false;
    ee.setVisible(ee.getControl("panel132"), false);
    ee.setVisible(ee.getControl("panel133"), false);
    ee.setVisible(ee.getControl("panel128"), false);
    ee.setVisible(ee.getControl("panel129"), false);
    ee.setTextColor(ee.getControl("layoutxitem15"), "#808080");
    ee.setTextColor(ee.getControl("layoutxitem16"), "#808080");
    if (pageIndex == 0) {
        ee.setVisible(ee.getControl("panel132"), true);
        ee.setVisible(ee.getControl("panel128"), true);
        ee.setTextColor(ee.getControl("layoutxitem15"), "#1C94E0");
    } else if (pageIndex == 1) {
        ee.setVisible(ee.getControl("panel133"), true);
        ee.setVisible(ee.getControl("panel129"), true);
        ee.setTextColor(ee.getControl("layoutxitem16"), "#1C94E0");
    }
}
g5.changePage2(0);

g5.refreshUserInfo = function() {
    var s;
    if (eb.isEmpty(g5.userName)) return;
    s = "<b>" + g5.nickName + "</b><br/>";
    s += "@" + g5.userName;
    ee.setText(ee.getControl("panel31"), s);
    ee.setEditText(ee.getControl("edit2"), g5.nickName);
    ee.setEditText(ee.getControl("edit3"), g5.userNote);
    ee.setEditText(ee.getControl("edit4"), g5.userHomepage);

    if (g5.photo == "") {
        ee.setImage(ee.getControl("panel8"), "/twitterG/main/user.png");
        ee.setImage(ee.getControl("panel29"), "/twitterG/main/user.png");
        ee.setImage(ee.getControl("panel47"), "/twitterG/main/user.png");
        ee.setImage(ee.getControl("panel59"), "/twitterG/main/user.png");
        ee.setImage(ee.getControl("panel14"), "/twitterG/main/user.png");
        ee.setImage(ee.getControl("panel111"), "/twitterG/main/user.png");
    } else {
        ee.setImage(ee.getControl("panel8"), "url:" + g5.imgUrl + g5.photo);
        ee.setImage(ee.getControl("panel29"), "url:" + g5.imgUrl + g5.photo);
        ee.setImage(ee.getControl("panel47"), "url:" + g5.imgUrl + g5.photo);
        ee.setImage(ee.getControl("panel59"), "url:" + g5.imgUrl + g5.photo);
        ee.setImage(ee.getControl("panel14"), "url:" + g5.imgUrl + g5.photo);
        ee.setImage(ee.getControl("panel111"), "url:" + g5.imgUrl + g5.photo);
    }
    if (g5.titleBg == "") {
        ee.setImage(ee.getControl("panel58"));
    } else {
        ee.setImage(ee.getControl("panel58"), "url:" + g5.imgUrl + g5.titleBg, "trim");
    }
}

g5.appendItem = function(parent, startY, hd, username, nickname, times, contentId, userPhoto, photo1, text, label1, label2, label3,
    reTimes, reContent, rePhoto1, reUserId, reUserName, reNickName, rePhoto) {
    if (eb.isEmpty(userPhoto)) userPhoto = "";
    if (eb.isEmpty(photo1)) photo1 = "";
    var w = eb.getDivRect(parent).w;
    if (eb.isEmpty(startY)) startY = 0;
    y = startY + 2; //v-space

    //root:v_userPhoto,v_titleText,v_text,v_btn1,v_btn2,v_btn3,v_btn4,v_label1,v_label2,v_label3
    //v_contentId
    var c;
    c = createPanel(parent, hd+"panel12", "FFFFFF", 0, y, w, 100);
