        c = createPanel($g(hd+"panel18"), hd+"panel20", "", 0, 0, 415, 234);
        addSkin(c, "/twitterG/skins/bodyMask.png","9x",57,56,19,20,20,18,19,20,20,18);
        eb.setDock(c,"fill","fill");
        c.style.pointerEvents = "none";
