        c = createPanel($g(hd+"panel2"), hd+"panel6", "", 0, 0, p1w, 100);
        eb.setDock(c,"disabled","disabled");
        c = createPanel($g(hd+"panel6"), hd+"panel8", "", p1w*0.0289156626506024, p1h*0.044585987261146, p1w*0.0987951807228916, p1w*0.0987951807228916);
