        c = createPanel($g(hd+"panel2"), hd+"panel7", "", 0, 0, p1w, p1y);
        addSkin(c, "/twitterG/skins/bodyMask1.png","9x",57,56,19,20,20,18,19,20,20,18);
        eb.setDock(c,"disabled","disabled");
