        g5.newPhoto1 = "";
        ee.setEditText(ee.getControl("edit1"), "");
        ee.setImage(ee.getControl("panel52"), "/twitterG/main/empty.png");
        ee.setText(ee.getControl("panel45"), "转发");
        g5.uplinkType = "F";
        g5.uplinkId = sender.parentNode.v_contentId;
