    });
    c = createPanel($g(hd+"panel12"), hd+"panel23", "", w*0.616698292220114, cy, w*0.0645161290322581, w*0.062);
    root.v_btn3 = c;
    ee.setImage(c,"/twitterG/main/e3.png","fill","","")
