    eb.processDock()
    return root;
}

g5.itemsPage2 = new Array();
g5.appendItemPage2 = function(parent, username, nickname, userPhoto) {
    //v_username
    //c_userphoto, c_label
    var hd = "f" + g5.itemsPage2.length + "_";
    if (eb.isEmpty(userPhoto)) userPhoto = "";
    var w = eb.getDivRect(parent).w;
    var h = eb.getDivRect(parent).h;
    var y = 0;
    if (g5.itemsPage2.length > 0) {
        y = eb.getDivRect(g5.itemsPage2[g5.itemsPage2.length-1]).y + eb.getDivRect(g5.itemsPage2[g5.itemsPage2.length-1]).h;
    }
    y += 2; //v-space

    c = createPanel(parent, hd+"panel12", "FFFFFF", 0, y, w, parseInt(w/5.4875));
    var root = c;
    ee.setOnClick(c,function(sender,event) {
        showUserPage.userName=sender.v_username;
        showUserPage();
    });
    g5.itemsPage2.push(root);
    root.v_username = username;
    eb.setDock(c,"disabled","disabled");
    c = createPanel($g(hd+"panel12"), hd+"panel13", "", 13, 10, 57, 59);
    eb.setDock(c,"ratio","ratio");
    c.leftRatio=0.0296127562642369;
    c.widthRatio=0.129840546697039;
    c.topRatio=0.125;
    c.heightRatio=0.7375;
    c = createPanel($g(hd+"panel13"), hd+"panel14", "", 0, 0, 57, 59);
    root.c_userphoto = c;
    if (userPhoto == "") {
        ee.setImage(c,"/twitterG/main/user.png","trim","","")
    } else {
        ee.setImage(c,"url:" + g5.imgUrl + userPhoto,"trim","","")
    }
    eb.setDock(c,"fill","fill");
    eb.setOuter(c,1,1,1,1);
    c = createPanel($g(hd+"panel13"), hd+"panel15", "", 0, 0, 57, 59);
    ee.setImage(c,"/twitterG/main/page2/cirMask.png","trim","","")
    eb.setDock(c,"fill","fill");
    c = createPanel($g(hd+"panel12"), hd+"panel16", "", 86, 18, 347, 43);
    root.c_label = c;
    addText(c, "<b>" + nickname + "</b>@" + username, "left", "middle", "Helvetica", 9, "000000", "normal");
    eb.setFontSizeAuto(c, 20);
    eb.setDock(c,"ratio","ratio");
    c.leftRatio=0.195899772209567;
    c.widthRatio=0.790432801822323;
    c.topRatio=0.225;
    c.heightRatio=0.5375;

    eb.processDock();
}

g5.getMins = function(mins) {
    mins = parseInt(mins);
    if (mins<60) {
        return mins+"分钟";
    } else if (mins<60*24) {
        return parseInt(mins/60) + "小时" + (mins%60) + "分钟";
    } else {
        return parseInt(mins/(60*24)) + "天" + parseInt((mins%(60*24))/60) + "小时";
    }
}

g5.refreshPage1 = function() {
    if (eb.isEmpty(g5.datasetHome)) return;
    var i, item;
    var root = ee.getControl("panel41"); 
    //new user ?
    if (g5.datasetHome.rowCount <= 0) {
        g5.newUser = true;
        ee.showAni(ee.getControl("layouty4"), 0);
        searchUser();
        return;    
    }
    g5.newUser = false;

    var cy = 0;
    if (updateHomepage.needRefresh) {
        for (i=0; i<g5.itemsHome.length; i++) ee.removeControl(g5.itemsHome[i]);
        g5.itemsHome = new Array();
    } else {
        if (g5.itemsHome.length>0) {
            var rect = eb.getDivRect(g5.itemsHome[g5.itemsHome.length-1]);
            cy = 2 + rect.y + rect.h;
        }
    }
    for (i=0; i< g5.datasetHome.rowCount; i++) {
        //"id,mins,userId,content,photo1,replyCount,forwardCount,likeCount,userName,nickName,photo" +
        //",reMins(11),reContent,rePhoto1,reUserId,reUserName,reNickName,rePhoto"
        //    reTimes, reContent, rePhoto1, reUserId, reUserName, reNickName, rePhoto
        item = g5.appendItem(
            root,
            cy, "page1_" + g5.itemsHome.length + "_",
            g5.datasetHome.getData(8,i),
            g5.datasetHome.getData(9,i),
            g5.getMins(g5.datasetHome.getData(1,i)),
            g5.datasetHome.getData(0,i),
            g5.datasetHome.getData(10,i),
            g5.datasetHome.getData(4,i),
            g5.datasetHome.getData(3,i),
            g5.datasetHome.getData(5,i),
            g5.datasetHome.getData(6,i),
            g5.datasetHome.getData(7,i),
            g5.getMins(g5.datasetHome.getData(11,i)),
            g5.datasetHome.getData(12,i),
            g5.datasetHome.getData(13,i),
            g5.datasetHome.getData(14,i),
            g5.datasetHome.getData(15,i),
            g5.datasetHome.getData(16,i),
            g5.datasetHome.getData(17,i)
            );
        g5.itemsHome.push(item);
        cy += 2 + eb.getDivRect(item).h;    
    }
    eb.setDivRect(ee.getControl("panel135"),null,null,null,cy);
    eb.setDivRect(ee.getControl("panel41"),null,null,null,cy);
}

g5.refreshPage2 = function() {
    if (eb.isEmpty(g5.datasetPage2)) return;
    var i;
    var root = ee.getControl("panel128"); 
    if (g5.newUser) {
        root = ee.getControl("layoutyitem8");
    }

    for (i=0; i<g5.itemsPage2.length; i++) ee.removeControl(g5.itemsPage2[i]);
    g5.itemsPage2 = new Array();
    for (i=0; i< g5.datasetPage2.rowCount; i++) {
        //function(parent, username, nickname, userPhoto)
        //dataset: id,userName,nickName,photo"
        g5.appendItemPage2(
            root,
            g5.datasetPage2.getData(1,i),
            g5.datasetPage2.getData(2,i),
            g5.datasetPage2.getData(3,i));
    }    
}

