var cy = 0; 
if (eb.isEmpty(searchTwitter.items)) {
    searchTwitter.items = new Array();
}
if (searchTwitter.needRefresh) {
    for (i=0; i<searchTwitter.items.length; i++) ee.removeControl(searchTwitter.items[i]);
    searchTwitter.items = new Array();
} else {
    if (searchTwitter.items.length>0) {
        var rect = eb.getDivRect(searchTwitter.items[searchTwitter.items.length-1]);
        cy = 2 + rect.y + rect.h;
    }
}
for (i=0; i< searchTwitter.dataset.rowCount; i++) {
    //"id,mins,userId,content,photo1,replyCount,forwardCount,likeCount,userName,nickName,photo" +
    //",reMins(11),reContent,rePhoto1,reUserId,reUserName,reNickName,rePhoto"
    //    reTimes, reContent, rePhoto1, reUserId, reUserName, reNickName, rePhoto
    item = g5.appendItem(
        root,
        cy, "p12_" + searchTwitter.items.length + "_",
        searchTwitter.dataset.getData(8,i),
        searchTwitter.dataset.getData(9,i),
        g5.getMins(searchTwitter.dataset.getData(1,i)),
        searchTwitter.dataset.getData(0,i),
        searchTwitter.dataset.getData(10,i),
        searchTwitter.dataset.getData(4,i),
        searchTwitter.dataset.getData(3,i),
        searchTwitter.dataset.getData(5,i),
