control.onClick:
var x = 100;
var y = 100;
var r = 50;
var progress = 0.99;

var path =  $g("ring");

// 计算当前的进度对应的角度值
var degrees = 90 + 360 - progress * 360;  

// 计算当前角度对应的弧度值
var rad = degrees* (Math.PI / 180);

//极坐标转换成直角坐标
var dx = (x+(Math.cos(rad) * r)).toFixed(2);
var dy = (y-(Math.sin(rad) * r)).toFixed(2);

//大于180度时候画大角度弧，小于180度的画小角度弧，(deg > 180) ? 1 : 0
var lenghty = window.Number(progress>0.5);

//path 属性
var descriptions = ["M", x, (y-r)+"", "A", r, r, 0, lenghty, 1, dx, dy];

// 给path 设置属性
path.setAttribute("d", descriptions.join(" "));

control.onCreate:
sender.innerHTML = "<svg width=\"500px\" height=\"500px\"><path id=\"ring\" fill=\"none\" stroke=\"#fd30ae\" style='stroke-width: 10px;'></svg>";
sender.refresh = function(progress) {
    var x = 100;
    var y = 100;
    var r = 50;
    var progress = 0.99;

    var path =  $g("ring");

    // 计算当前的进度对应的角度值
    var degrees = 90 + 360 - progress * 360;  

    // 计算当前角度对应的弧度值
    var rad = degrees* (Math.PI / 180);

    //极坐标转换成直角坐标
    var dx = (x+(Math.cos(rad) * r)).toFixed(2);
    var dy = (y-(Math.sin(rad) * r)).toFixed(2);

    //大于180度时候画大角度弧，小于180度的画小角度弧，(deg > 180) ? 1 : 0
    var lenghty = window.Number(progress>0.5);

    //path 属性
    var descriptions = ["M", x, (y-r)+"", "A", r, r, 0, lenghty, 1, dx, dy];

    // 给path 设置属性
    path.setAttribute("d", descriptions.join(" "));

}

control.onClick:
var path =  $g("ring");
var descriptions = ["M", 100,100, "L", 400,400];
path.setAttribute("d", descriptions.join(" "));
