taskSetItem.script2:
var token = eio.readString8();
var history = eio.readInt32();
var teamId = eio.readInt32();
var sql,sqlResult;

sql = "exec getUserInfoBySession '" + db.encode(token) + "'";
sqlResult = db.executeQuery("PJ", sql);
if (sqlResult == null) {
    eio.appendString8("F");
    es.logWarning("系统繁忙请稍候再试");
    return;
}
db.read(sqlResult);
if (db.getString(sqlResult, "sessionId").length < 5) {
    eio.appendString8("F");
    es.logWarning("用户token失效, sql=" + sql);
    return;
}
var userId = db.getString(sqlResult, "id");

if (history == 1) {
    sql = "select taskName, convert(varchar(100), closeDatetime, 23) " +
        "+ ',' + cast(datediff(hour, regDatetime, closeDatetime) as varchar(10)) as [time], id " +
        "from taskInfo where status = 'CLS' and teamId = " + teamId + " order by closeDatetime desc ";
} else {
    sql = "select taskName, datediff(hour, regDatetime, getdate()) as [time], id " +
        "from taskInfo where status = 'NEW' and teamId = " + teamId + " order by regDatetime desc ";
}
sqlResult = db.executeQuery("PJ", sql);
if (sqlResult == null) {
    eio.appendString8("F");
    return;
}
eio.appendString8("T");
eio.appendDataSet(sqlResult,"taskName,time,id",0,1000);

var taskId = eio.readInt32();
var sql,sqlResult;
sql = 
    "SELECT dbo.taskInfo.*, dbo.userInfo.*, userInfo_1.idName as closerIdName, " +
    "  userInfo_1.mobile as closerMobile " +
    "FROM dbo.taskInfo INNER JOIN " +
    "  dbo.userInfo ON dbo.taskInfo.creator = dbo.userInfo.id " +
    "  LEFT OUTER JOIN dbo.userInfo AS userInfo_1 ON dbo.taskInfo.closer = userInfo_1.id " +
    "where dbo.taskInfo.id = " + taskId
sqlResult = db.executeQuery("PJ", sql);
if (sqlResult == null) {
    eio.appendString8("F");
    return;
}
if (!db.read(sqlResult)) {
    eio.appendString8("F");
    return;
}
var idName = db.getString(sqlResult, "idName");
var taskName = db.getString(sqlResult, "taskName");
var status = db.getString(sqlResult, "status");    
var regDatetime = db.getString(sqlResult, "regDatetime");
var closeDatetime = db.getString(sqlResult, "closeDatetime");
var mobile = db.getString(sqlResult, "mobile");
var closerIdName = db.getString(sqlResult, "closerIdName");
var closerMobile = db.getString(sqlResult, "closerMobile");

sql =
    "SELECT dbo.userInfo.idName + '(' + dbo.userInfo.mobile + ')：' + dbo.taskChat.title " +
    "   AS title, CAST(DATEPART(year, dbo.taskChat.regDatetime) AS varchar(10)) " + 
    "  + '年' + CAST(DATEPART(month, dbo.taskChat.regDatetime) AS varchar(10)) " + 
    "  + '月' + CAST(DATEPART(day, dbo.taskChat.regDatetime) AS varchar(10)) " + 
    "  + '日' + CAST(DATEPART(hour, dbo.taskChat.regDatetime) AS varchar(10)) " + 
    "  + '点' AS regDatetime " +
    "FROM dbo.taskChat INNER JOIN " +
    "  dbo.userInfo ON dbo.taskChat.userId = dbo.userInfo.id " +
    "WHERE (dbo.taskChat.taskId = " + taskId + ") " +
    "ORDER BY dbo.taskChat.regDatetime DESC";
sqlResult = db.executeQuery("PJ", sql);
if (sqlResult == null) {
    eio.appendString8("F");
    return;
}
eio.appendString8("T");
eio.appendString8(idName);
eio.appendString16(taskName);
eio.appendString8(status);
eio.appendString8(regDatetime);
eio.appendString8(closeDatetime);
eio.appendString8(mobile);
eio.appendString8(closerIdName);
eio.appendString8(closerMobile);
eio.appendDataSet(sqlResult,"title,regDatetime",0,1000);

