control.onControlLoad:
var isIOS = !!navigator.userAgent.match(/\(i[^;]+;( U;)? CPU.+Mac OS X/);
if (!isIOS) {
    ee.getControl("layoutyitem1").heavy = 0;
    ee.getControl("layoutyitem8").heavy = 0;
}
setTimeout(function(){
    ee.autoResize = false;
},1000);

g5.history = false;
getUserInfo();

//20秒刷新一次任务列表
setInterval(function() {
    projectRefresh();
}, 20000);

control.onUpdate:
var hour,time = "";
if (text.split(",").length==2) {
    time = text.split(",")[0];
    hour = parseInt(text.split(",")[1]);
} else {
    hour = parseInt(text);
}
if (hour<24) {
    text = hour + "小时";
} else if (hour==24) {
    text = "1天";
} else if (hour<48) {
    text = "1天零" + (hour-24) + "小时";
} else {
    text = parseInt(hour/24) + "天";
}
if (time.length>0) {
    text = time + "</br>" + text;
}
ee.setText(sender, text);

control.onClick:
if (g5.history) {
    ee.setText(sender, "历史记录");
    ee.setText(ee.getControl("panel10"), "进行中的任务");
} else {
    ee.setText(sender, "进行中的任务");
    ee.setText(ee.getControl("panel10"), "历史记录");
}

g5.history = !g5.history;
projectRefresh();

ee.setVisible(ee.getControl("panel20"), false);
ee.setVisible(ee.getControl("panel1"), false);
ee.showAni(ee.getControl("panel45"));
ee.setEditText(ee.getControl("edit3"), "");
ee.setEditText(ee.getControl("edit4"), "");

ee.setVisible(ee.getControl("panel20"), false);
ee.setVisible(ee.getControl("panel1"), false);
ee.showAni(ee.getControl("panel54"));
ee.setEditText(ee.getControl("edit5"), "");

if (!confirm("您确定本项目已经完成或者由于某种原因导致无法继续？")) {
    return;
}
projectClose();

