taskSetItem.script2:
var history = eio.readInt32();
var sql,sqlResult;
if (history == 1) {
    sql = "select projectName, convert(varchar(100), closeDatetime, 23) " +
        "+ ',' + cast(datediff(hour, regDatetime, closeDatetime) as varchar(10)) as [time], id " +
        "from projectInfo where status = 'CLS' order by closeDatetime desc ";
} else {
    sql = "select projectName, datediff(hour, regDatetime, getdate()) as [time], id " +
        "from projectInfo where status = 'NEW' order by regDatetime desc ";
}
sqlResult = db.executeQuery("PJ", sql);
if (sqlResult == null) {
    eio.appendString8("F");
    return;
}
eio.appendString8("T");
eio.appendDataSet(sqlResult,"projectName,time,id",0,1000);

var projectId = eio.readInt32();
var sql,sqlResult;
sql = 
    "SELECT dbo.projectInfo.*, dbo.userInfo.*, userInfo_1.idName as closerIdName, " +
    "  userInfo_1.mobile as closerMobile " +
    "FROM dbo.projectInfo INNER JOIN " +
    "  dbo.userInfo ON dbo.projectInfo.creator = dbo.userInfo.id " +
    "  LEFT OUTER JOIN dbo.userInfo AS userInfo_1 ON dbo.projectInfo.closer = userInfo_1.id " +
    "where dbo.projectInfo.id = " + projectId;
sqlResult = db.executeQuery("PJ", sql);
if (sqlResult == null) {
    eio.appendString8("F");
    return;
}
if (!db.read(sqlResult)) {
    eio.appendString8("F");
    return;
}
var idName = db.getString(sqlResult, "idName");
var projectName = db.getString(sqlResult, "projectName");
var status = db.getString(sqlResult, "status");    
var regDatetime = db.getString(sqlResult, "regDatetime");
var closeDatetime = db.getString(sqlResult, "closeDatetime");
var mobile = db.getString(sqlResult, "mobile");
var closerIdName = db.getString(sqlResult, "closerIdName");
var closerMobile = db.getString(sqlResult, "closerMobile");

sql =
    "SELECT dbo.userInfo.idName + '(' + dbo.userInfo.mobile + ')：' + dbo.projectChat.title " +
    "   AS title, CAST(DATEPART(year, dbo.projectChat.regDatetime) AS varchar(10)) " + 
    "  + '年' + CAST(DATEPART(month, dbo.projectChat.regDatetime) AS varchar(10)) " + 
    "  + '月' + CAST(DATEPART(day, dbo.projectChat.regDatetime) AS varchar(10)) " + 
    "  + '日' + CAST(DATEPART(hour, dbo.projectChat.regDatetime) AS varchar(10)) " + 
    "  + '点' AS regDatetime " +
    "FROM dbo.projectChat INNER JOIN " +
    "  dbo.userInfo ON dbo.projectChat.userId = dbo.userInfo.id " +
    "WHERE (dbo.projectChat.projectId = " + projectId + ") " +
    "ORDER BY dbo.projectChat.regDatetime DESC";
sqlResult = db.executeQuery("PJ", sql);
if (sqlResult == null) {
    eio.appendString8("F");
    return;
}
eio.appendString8("T");
eio.appendString8(idName);
eio.appendString16(projectName);
eio.appendString8(status);
eio.appendString8(regDatetime);
eio.appendString8(closeDatetime);
eio.appendString8(mobile);
eio.appendString8(closerIdName);
eio.appendString8(closerMobile);
eio.appendDataSet(sqlResult,"title,regDatetime",0,1000);

