sqlResult = db.executeQuery("PJ", sql);
if (sqlResult == null) {
    eio.appendString8("F");
    es.logWarning("数据库执行错误, sql=" + sql);
    return;
}
if (db.read(sqlResult)) {
    eio.appendString8("T");
    return;
} 

//获取idName
sql = "exec getUserInfoBySession '" + db.encode(token) + "'";
sqlResult = db.executeQuery("PJ", sql);
if (sqlResult == null) {
    eio.appendString8("F");
    es.logWarning("数据库执行错误, sql=" + sql);
    return;
}
db.read(sqlResult);
if (db.getString(sqlResult, "sessionId").length < 5) {
    eio.appendString8("F");
    es.logWarning("用户token失效, sql=" + sql);
    return;
} 
var idName = db.getString(sqlResult,"idName");

//获取系统网址
sql = "select * from params where [name] = 'www'";
sqlResult = db.executeQuery("PJ", sql);
if (sqlResult == null) {
    eio.appendString8("F");
    es.logWarning("数据库执行错误, sql=" + sql);
    return;
}
if (!db.read(sqlResult)) {
    eio.appendString8("F");
    es.logWarning("数据库执行错误, sql=" + sql);
    return;
}
var www = db.getString(sqlResult,"value");

//发送短信
sql = "exec sendSms '" + mobile + "','" +
    "您的好友" + idName + "邀请您加入项目组，请打开网址" + www + " 并注册进入" + "';";
sqlResult = db.executeNoneQuery("PJ", sql);
if (!sqlResult) {
    eio.appendString8("F");
    es.logWarning("数据库执行错误, sql=" + sql);
    return;
}
eio.appendString8("T");

